var lat = new Array(0);
var lng = new Array(0);
var markerwindowinfo = new Array(0);
var markerevent = new Array(0);
var markerlabel = new Array(0);
var markertype = new Array(0);
var markershow = new Array(0);
var firstshown = new Array(0);
var marker = new Array(0);

function hpbmaponload() {
	var map = new Y.Map("HPBMAP_20190826020608");
	map.drawMap(new Y.LatLng(37.525275, 139.870727),16,Y.LayerSetId.NORMAL);
	map.removeLayerSet(Y.LayerSetId.B1);
	map.addControl(new Y.ScaleControl());
	map.addControl(new Y.SliderZoomControlVertical());
	map.addControl(new Y.HomeControl());
	map.addControl(new Y.CenterMarkControl({visibleButton:true,visible:true}), new Y.ControlPosition(Y.ControlPosition.TOP_RIGHT, new Y.Size(27, 3)));
	map.addControl(new Y.LayerSetControl(), new Y.ControlPosition(Y.ControlPosition.TOP_RIGHT, new Y.Size(48, 3)));
	return map;
}